using BloomFilter.HashAlgorithms.Internal;
using System;
using System.Collections.Generic;
using Xunit;
using Xunit.Abstractions;

namespace BloomFilterTest.HashAlgorithms
{
    public class FNVTest
    {
        private readonly ITestOutputHelper _testOutputHelper;

        public FNVTest(ITestOutputHelper testOutputHelper)
        {
            _testOutputHelper = testOutputHelper;
        }

        [Fact]
        public void FNV1_Test()
        {
            var tests = FNV1TestData();

            foreach (var test in tests)
            {
                var hash = FNV1.HashToUInt32(test.Hex.FromHex());

                Assert.Equal(test.Hash, hash);
            }
        }

        [Fact]
        public void FNV1_Append()
        {
            var tests = FNV1TestData();

            foreach (var test in tests)
            {
                var fnv = new FNV1();

                Span<byte> span = test.Hex.FromHex();

                while (span.Length > 0)
                {
                    var n = Random.Shared.Next(1, span.Length);
                    var d = span.Slice(0, n);
                    fnv.Append(d);
                    span = span.Slice(n);
                }

                var hash2 = fnv.GetCurrentHashAsUInt32();

                Assert.Equal(test.Hash, hash2);

                var hash3 = fnv.GetCurrentHashAsUInt32();

                Assert.Equal(test.Hash, hash3);
            }
        }

        [Fact]
        public void FNV1a_Test()
        {
            var tests = FNV1aTestData();

            foreach (var test in tests)
            {
                var hash = FNV1a.HashToUInt32(test.Hex.FromHex());

                Assert.Equal(test.Hash, hash);
            }
        }

        [Fact]
        public void FNV1a_Append()
        {
            var tests = FNV1aTestData();

            foreach (var test in tests)
            {
                var fnv = new FNV1a();

                Span<byte> span = test.Hex.FromHex();

                while (span.Length > 0)
                {
                    var n = Random.Shared.Next(1, span.Length);
                    var d = span.Slice(0, n);
                    fnv.Append(d);
                    span = span.Slice(n);
                }

                var hash2 = fnv.GetCurrentHashAsUInt32();

                Assert.Equal(test.Hash, hash2);

                var hash3 = fnv.GetCurrentHashAsUInt32();

                Assert.Equal(test.Hash, hash3);
            }
        }

